let baseUrl='https://www.feuerwehr-abschnitt-jauntal.at/articles/';var number_of_articles=1;var number_of_visible_articles=0;var number_of_loaded_articles=0;const show_article_images=!1;var articles=[];function loadContent(){var xhttp=new XMLHttpRequest();xhttp.onreadystatechange=function(){if(this.readyState==4&&this.status==200){var a=JSON.parse(this.responseText);number_of_articles=a.NumberOfArticles;number_of_visible_articles=a.NumberOfVisibleArcticles;loadArticles()}};xhttp.open("GET",baseUrl+'settings.json',!0);xhttp.send()}
