function loadArticle(id){var xhttp=new XMLHttpRequest();xhttp.onreadystatechange=function(){if(this.readyState==4&&this.status==200){var article=JSON.parse(this.responseText);articles.push(article);number_of_loaded_articles++;if(number_of_loaded_articles<number_of_visible_articles){number_of_articles--;loadArticle(number_of_articles)}else{visualizeArticles()}}};xhttp.open("GET",baseUrl+id+'/'+id+'.json',!0);xhttp.send()}
